    def analyze_user_query(self, user_query: str) -> Dict:
        """Analyze user query and determine relevant tables"""
        query_lower = user_query.lower()
        
        # Remove common words
        stop_words = {"the", "a", "an", "and", "or", "but", "in", "on", "at", "to", "for", "of", "with", "by", "is", "are", "was", "were", "be", "been", "have", "has", "had", "do", "does", "did", "will", "would", "could", "should", "may", "might", "can", "must"}
        
        words = [word for word in query_lower.split() if word not in stop_words and len(word) > 2]
        
        relevant_tables = set()
        matched_patterns = []
        confidence_scores = {}
        
        # Check each query pattern
        for query_type, pattern_info in self.query_patterns.items():
            score = 0
            pattern_matches = []
            
            for pattern in pattern_info['patterns']:
                if pattern in query_lower:
                    score += 3  # Higher score for exact pattern match
                    pattern_matches.append(pattern)
                    # Add primary tables first (they get priority)
                    relevant_tables.update(pattern_info['primary_tables'])
                    
                    # Be more selective about secondary tables
                    if score >= 3:
                        # Only add specific secondary tables for certain queries
                        if query_type == 'employee_queries':
                            # For department-specific queries, include department tables
                            if any(dept_word in query_lower for dept_word in ['department', 'dept', 'team', 'group', 'division']):
                                relevant_tables.update(pattern_info.get('department_tables', []))
                            # For permission-related queries, include permission tables  
                            elif any(perm_word in query_lower for perm_word in ['permission', 'access', 'role', 'can do', 'allowed']):
                                relevant_tables.update(pattern_info.get('permission_tables', []))
                            # For simple employee lists, keep it minimal
                            elif any(simple_word in query_lower for simple_word in ['show', 'list', 'all employees', 'staff list']):
                                pass  # Don't add secondary tables for simple lists
                            else:
                                # Add limited secondary tables for other employee queries
                                relevant_tables.update(pattern_info.get('secondary_tables', [])[:1])  # Only 1 secondary table
                        else:
                            # For non-employee queries, add secondary tables as before but limited
                            relevant_tables.update(pattern_info.get('secondary_tables', [])[:2])  # Limit secondary tables
            
            # Check for word matches (lower priority)
            for word in words:
                for pattern in pattern_info['patterns']:
                    if word in pattern or pattern in word:
                        score += 1
                        pattern_matches.append(f"word_match: {word}")
                        # Only add primary tables for word matches
                        relevant_tables.update(pattern_info['primary_tables'][:2])  # Limit to 2 primary tables for word matches
            
            if score > 0:
                confidence_scores[query_type] = score
                matched_patterns.extend(pattern_matches)
        
        # If no specific patterns matched, include core tables based on common words
        if not relevant_tables:
            # Smart fallback based on common query words
            if any(word in query_lower for word in ['employee', 'staff', 'person', 'who', 'team']):
                relevant_tables.update(['tblstaff', 'employees'])
            elif any(word in query_lower for word in ['project', 'proj']):
                relevant_tables.update(['tblprojects', 'projects'])
            elif any(word in query_lower for word in ['task', 'todo', 'assignment']):
                relevant_tables.update(['tbltasks', 'tasks'])
            elif any(word in query_lower for word in ['client', 'customer', 'contact']):
                relevant_tables.update(['tblclients', 'tblcontacts'])
            else:
                # Ultimate fallback - most useful core tables
                relevant_tables.update(['tblstaff', 'tblprojects', 'tbltasks'])
            matched_patterns.append('smart_fallback_tables')
        
        return {
            'relevant_tables': list(relevant_tables),
            'matched_patterns': matched_patterns,
            'confidence_scores': confidence_scores,
            'query_analysis': {
                'original_query': user_query,
                'processed_words': words,
                'dominant_intent': max(confidence_scores.items(), key=lambda x: x[1])[0] if confidence_scores else 'general'
            }
        }
    
    def get_table_info(self, table_name: str) -> Dict:
        """Get information about a specific table"""
        for category, info in self.table_categories.items():
            for table in info['tables']:
                if table['name'] == table_name:
                    return {
                        'name': table_name,
                        'category': category,
                        'columns': table['columns'],
                        'key_columns': table['key_columns'],
                        'row_count': table['row_count']
                    }
        return None
    
    def get_optimized_query_strategy(self, user_query: str) -> Dict:
        """Get optimized querying strategy for user query"""
        analysis = self.analyze_user_query(user_query)
        
        strategy = {
            'primary_tables': [],
            'secondary_tables': [],
            'search_columns': {},
            'join_strategy': [],
            'limit_per_table': 50
        }
        
        # Prioritize tables based on confidence and importance
        high_confidence_tables = []
        medium_confidence_tables = []
        
        # Get the dominant intent
        confidence_scores = analysis['confidence_scores']
        dominant_intent = max(confidence_scores.items(), key=lambda x: x[1])[0] if confidence_scores else None
        
        for table in analysis['relevant_tables']:
            table_info = self.get_table_info(table)
            if table_info:
                # Prioritize tables from the dominant intent pattern
                is_primary_table = False
                if dominant_intent and dominant_intent in self.query_patterns:
                    pattern_info = self.query_patterns[dominant_intent]
                    is_primary_table = table in pattern_info.get('primary_tables', [])
                
                # High confidence: primary tables from dominant intent with good row count
                if is_primary_table and table_info['row_count'] > 0:
                    high_confidence_tables.append(table)
                # Medium confidence: other tables or primary tables with no data
                elif table_info['row_count'] > 5:  # Only include tables with some data
                    medium_confidence_tables.append(table)
        
        # Prioritize by row count within each confidence level
        high_confidence_tables.sort(key=lambda t: self.get_table_info(t)['row_count'], reverse=True)
        medium_confidence_tables.sort(key=lambda t: self.get_table_info(t)['row_count'], reverse=True)
        
        strategy['primary_tables'] = high_confidence_tables[:2]  # Limit to 2 primary
        strategy['secondary_tables'] = medium_confidence_tables[:2]  # Limit to 2 secondary
        
        # Define search columns for each table
        query_words = user_query.lower().split()
        for table in strategy['primary_tables'] + strategy['secondary_tables']:
            table_info = self.get_table_info(table)
            if table_info:
                # Smart column selection
                search_cols = []
                for col in table_info['columns']:
                    col_lower = col.lower()
                    if any(word in col_lower for word in ['name', 'title', 'description', 'email']):
                        search_cols.append(col)
                
                if not search_cols:  # Fallback to key columns
                    search_cols = table_info['key_columns'][:3]
                
                strategy['search_columns'][table] = search_cols[:5]  # Limit columns
        
        return strategy

